import React, { useState, useEffect } from 'react';
import axios from 'axios';

const UserList = () => {
    const [users, setUsers] = useState([]);

    // Fetch all users when component mounts
    useEffect(() => {
        fetchUsers();
    }, []);

    const fetchUsers = () => {
        axios.get('http://localhost:5000/api/users')
            .then(response => {
                setUsers(response.data);

            })
            .catch(error => {
                console.log(error);
            });
    };

    // Delete a user
    const deleteUser = (id) => {
        axios.delete(`http://localhost:5000/api/users/${id}`)
            .then(() => {
                fetchUsers(); // Refresh the list after deletion
            })
            .catch(error => {
                console.log(error);
            });
    };

    // Edit a user (for now, we'll use a simple prompt, you can implement a form for editing)
    const editUser = (id) => {
        const newName = prompt('Enter new name:');
        const newEmail = prompt('Enter new email:');
        const newAge = prompt('Enter new age:');

        axios.put(`http://localhost:5000/api/users/${id}`, {
            name: newName,
            email: newEmail,
            age: newAge
        })
        .then(() => {
            fetchUsers(); // Refresh the list after updating
        })
        .catch(error => {
            console.log(error);
        });
    };

    return (
        <div>
            <h2>User List</h2>
            <table>
                <thead>
                    <tr>
                        <th>Name</th>
                        <th>Email</th>
                        <th>Age</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {users.map(user => (
                        <tr key={user.id}>
                            <td>{user.name}</td>
                            <td>{user.email}</td>
                            <td>{user.age}</td>
                            <td>
                                <button onClick={() => editUser(user.id)}>Edit</button>
                                <button onClick={() => deleteUser(user.id)}>Delete</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default UserList;
